        exit 1
    fi
    export FAL_API_KEY="$API_KEY"
fi

echo -e "${GREEN}✅ API Key bulundu${NC}"
echo ""

# Test görseli için base64 data URI (küçük test görseli)
# Not: Gerçek test için bir görsel dosyası kullanmalısınız
TEST_IMAGE_BASE64="data:image/jpeg;base64,"

