━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━ data:image/jpeg;base64,"

# Request body oluştur
REQUEST_BODY=$(cat <<EOF
{
  "prompt": "$PROMPT",
  "image_size": "portrait_4_3",
  "num_images": 1,
  "image_url": "$TEST_IMAGE_BASE64",
  "strength": 0.8,
  "guidance_scale": 7.5
}
EOF
)

echo -e "${YELLOW}📤 Fal AI API'ye istek gönderiliyor...${NC}"
echo ""

# Submit request
SUBMIT_RESPONSE=$(curl -s -w "\n%{http_code}" -X POST "https://queue.fal.run/fal-ai/flux/dev" \
  -H "Authorization: Key $FAL_API_KEY" \
  -H "Content-Type: application/json" \
  -d "$REQUEST_BODY")

HTTP_CODE=$(echo "$SUBMIT_RESPONSE" | tail -n1)
RESPONSE_BODY=$(echo "$SUBMIT_RESPONSE" | sed '$d')

if [ "$HTTP_CODE" != "200" ] && [ "$HTTP_CODE" != "201" ]; then
    echo -e "${RED}❌ HATA: API isteği başarısız!${NC}"
    echo -e "${RED}HTTP Status: $HTTP_CODE${NC}"
    echo -e "${YELLOW}Response:${NC}"
    echo "$RESPONSE_BODY" | jq '.' 2>/dev/null || echo "$RESPONSE_BODY"
    exit 1
fi

echo -e "${GREEN}✅ İstek başarıyla gönderildi!${NC}"
echo ""

# Response'dan request_id ve status_url'i al
REQUEST_ID=$(echo "$RESPONSE_BODY" | jq -r '.request_id' 2>/dev/null)
STATUS_URL=$(echo "$RESPONSE_BODY" | jq -r '.status_url' 2>/dev/null)
RESPONSE_URL=$(echo "$RESPONSE_BODY" | jq -r '.response_url' 2>/dev/null)

if [ -z "$REQUEST_ID" ] || [ "$REQUEST_ID" = "null" ]; then
    echo -e "${RED}❌ HATA: request_id alınamadı!${NC}"
    echo -e "${YELLOW}Response:${NC}"
    echo "$RESPONSE_BODY" | jq '.' 2>/dev/null || echo "$RESPONSE_BODY"
    exit 1
fi

echo -e "${BLUE}📋 Request ID: ${GREEN}$REQUEST_ID${NC}"
echo -e "${BLUE}📋 Status URL: ${GREEN}$STATUS_URL${NC}"
echo -e "${BLUE}📋 Response URL: ${GREEN}$RESPONSE_URL${NC}"
echo ""

# Status polling
echo -e "${YELLOW}⏳ Sonuç bekleniyor (maksimum 60 saniye)...${NC}"
MAX_ATTEMPTS=60
ATTEMPT=0
STATUS="IN_QUEUE"

while [ $ATTEMPT -lt $MAX_ATTEMPTS ]; do
    sleep 2
    ATTEMPT=$((ATTEMPT + 1))
    
    STATUS_RESPONSE=$(curl -s -w "\n%{http_code}" \
      -H "Authorization: Key $FAL_API_KEY" \
      "$STATUS_URL")
    
    STATUS_HTTP_CODE=$(echo "$STATUS_RESPONSE" | tail -n1)
    STATUS_BODY=$(echo "$STATUS_RESPONSE" | sed '$d')
    
    if [ "$STATUS_HTTP_CODE" = "200" ]; then
        STATUS=$(echo "$STATUS_BODY" | jq -r '.status' 2>/dev/null || echo "UNKNOWN")
        
        if [ "$STATUS" = "COMPLETED" ]; then
            echo -e "${GREEN}✅ İşlem tamamlandı!${NC}"
            echo ""
            
            # Sonucu al
            RESULT_RESPONSE=$(curl -s -w "\n%{http_code}" \
              -H "Authorization: Key $FAL_API_KEY" \
              "$RESPONSE_URL")
            
            RESULT_HTTP_CODE=$(echo "$RESULT_RESPONSE" | tail -n1)
            RESULT_BODY=$(echo "$RESULT_RESPONSE" | sed '$d')
            
            if [ "$RESULT_HTTP_CODE" = "200" ]; then
                IMAGE_URL=$(echo "$RESULT_BODY" | jq -r '.images[0].url' 2>/dev/null)
                
                if [ -n "$IMAGE_URL" ] && [ "$IMAGE_URL" != "null" ]; then
                    echo -e "${GREEN}🎉 Resim başarıyla üretildi!${NC}"
                    echo -e "${BLUE}📸 Image URL: ${GREEN}$IMAGE_URL${NC}"
                    echo ""
                    echo -e "${YELLOW}💡 Resmi görüntülemek için:${NC}"
                    echo -e "   open $IMAGE_URL"
                else
                    echo -e "${RED}❌ HATA: Image URL bulunamadı!${NC}"
                    echo "$RESULT_BODY" | jq '.' 2>/dev/null || echo "$RESULT_BODY"
                fi
            else
                echo -e "${RED}❌ HATA: Sonuç alınamadı!${NC}"
                echo -e "${RED}HTTP Status: $RESULT_HTTP_CODE${NC}"
            fi
            exit 0
        elif [ "$STATUS" = "FAILED" ]; then
            echo -e "${RED}❌ İşlem başarısız!${NC}"
            echo "$STATUS_BODY" | jq '.' 2>/dev/null || echo "$STATUS_BODY"
            exit 1
        else
            echo -e "${YELLOW}⏳ Durum: $STATUS (${ATTEMPT}/${MAX_ATTEMPTS})${NC}"
        fi
    fi
done

echo -e "${RED}❌ Timeout: İşlem 60 saniye içinde tamamlanamadı!${NC}"
exit 1

